```php

Teenagers' Challenges
php
// Set up the characters and their attributes
$character1 = array(
"name" = "Emily",
"age" => 16,
"gender" => "female",
"relationship_status" => "single"
);
$character2 = array(
"name" => "Jack",
"age" => 17,
"gender" => "male",
"relationship_status" => "in a relationship"
);
// Set up the plot and scenes
$plot = array(
"scene1" => "Emily and Jack meet each other in the school hallway.",
"scene2" => "Emily and Jack talk about their classes and upcoming exams.",
"scene3" => "Emily and Jack both complain about their parents and family issues.",
"scene4" => "Emily and Jack go on a date to the movies.",
"scene5" => "Emily and Jack have a fight about their different relationship expectations."
);
// Display the story
